import VueRouter from "vue-router";
import Login from '../src/view/Login'
const router = new VueRouter({
    routes: [
        {
            path:'/',
            redirect:'/login'
        },
        {
            path:'/login',
            component:Login,
            meta:{title:'登录'}
        }
    ]
})
//后置路由守卫

router.afterEach((to,from)=>{
    document.title=to.meta.title

})

export default router
