import VueRouter from "vue-router";
import Login from '../src/view/Login'
import Home from '../src/view/home'
const router = new VueRouter({
    routes: [
        {
            path:'/',
            redirect:'/login'
        },
        {
            path:'/login',
            component:Login,
            meta:{title:'登录'}
        },
        {
            path:'/home',
            component:Home,
            meta:{title:'登录'}
        }
    ]
})
//后置路由守卫

router.afterEach((to)=>{
    document.title=to.meta.title ||'后台管理系统'

})

export default router
